module.exports = {
  getMetodos: function(propriedades) {
    for(var prop in propriedades) {
      (function(i) {
        this['get' + i] = function() {
          return propriedades[i];
        };
      }.bind(this))(prop);
    }
  },

  get: function(prop) {
    if(this['get' + prop]) {
      return this['get' + prop]();
    }
    return null;
  }
};
