var Node = require('./node');

var Fifo = function() {

  this.inicio = null;
  this.fim    = null;
};

Fifo.prototype.push = function(propriedades) {

  var novo = new Node(propriedades);
  
  if(this.inicio) {
    this.inicio.prox = novo;
  }
  else {
    this.fim = novo;
  }
  this.inicio = novo;
};

Fifo.prototype.each = function(callback) {

  (function eaching(node) {
    if(node !== null) {
      callback(node);
      eaching(node.prox);
    }
  }.bind(this))(this.inicio);
};


module.exports = Fifo;
